sys_script_include.script:
var BSI_Verify_Include = Class.create();
BSI_Verify_Include.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	config_subcat_bsi_query:function(){
		var obj = {};
		var bsi_number = this.getParameter("sysparm_bsi_number");

		var bsi = new GlideRecord("x_131279_business_bsi");
		bsi.addQuery("sys_id",bsi_number);
		bsi.query();
		while(bsi.next()){
		obj.config = bsi.getValue("trigger_configuration_item");
		obj.subcat = bsi.getValue("trigger_subcategory");
		}

		return JSON.stringify(obj);



	},


		type: 'BSI_Verify_Include'
	});
