sys_ui_page.client_script:
var button = document.getElementById('button');
console.log('button made');
button.onclick = function(){
	console.log('button clicked');
var requestBody = ""; 

var client=new XMLHttpRequest();
client.open("get","https://dev37520.service-now.com/api/now/table/x_131279_business_bsi?sysparm_limit=1");

client.setRequestHeader('Accept','application/json');
client.setRequestHeader('Content-Type','application/json');

//Eg. UserName="admin", Password="admin" for this code sample.
client.setRequestHeader('Authorization', 'Basic '+btoa('danpal01'+':'+'admin'));

client.onreadystatechange = function() { 
	if(this.readyState == this.DONE) {
		document.getElementById("response").innerHTML=this.status + this.response; 
		var respond = JSON.parse(this.response);
		document.getElementById('result').innerHTML = JSON.stringify(respond.result[0].number);
	}
}; 
client.send(requestBody);



};
